  Future _confirmar_hash_pagado(
      BuildContext context,
      CriptoResponse criptodata,
      String uid,
      String name,
      String fichas,
      String criptoselec,
      String totalt) async {
    //si el apgo esta completado activamos las monedas
    //las monedas las pedira al servidor antes de iniciar el jeugo siempre asi no podra hacer trampa en la app
    //peticion a node y luego aqui obtengo
    var headers = {'Content-Type': 'application/json'};
    var request = http.Request('POST',
        Uri.parse('http://salonhousev2.herokuapp.com/validarchargue/123'));
    request.body = json.encode({
      "uid": uid,
      "buyOrder": timestamp_obtenercodigo(),
      "name": name,
      "unidfichas": fichas, //1000
      "criptomonedaseleccionada": criptoselec,
      "total": criptodata.precios, // "1",
      "hash": criptodata.code, //"EVX5M3WE"
    });
    request.headers.addAll(headers);

    http.StreamedResponse response = await request.send();

    if (response.statusCode == 200) {
      print(await response.stream.bytesToString()); //[]
      guardar_pago_monedas(context, criptodata, uid, name, fichas, criptoselec,
          totalt, criptodata.code, cashselec);
    } else {
      print(response.reasonPhrase);
    }

    //
  }
